mapper.select:
select count(1)
from trick
where cb_code = #{code,jdbcType=VARCHAR}

SELECT count(1)
FROM trick t
         JOIN chess_info ci ON t.cb_code = ci.`code`
    AND ci.user_id = #{telephone,jdbcType=VARCHAR}
    AND HOUR(t.exute_time) BETWEEN #{prefix,jdbcType=INTEGER} AND #{suffix,jdbcType=INTEGER}
    AND t.exute_time > DATE_SUB(NOW(), INTERVAL 1 MONTH)

select count(0)
from trick
where cb_code = #{code}
  and exute_time between #{from} and #{to}

SELECT t.trick, t.color, DATE_FORMAT(t.exute_time, '%m-%d %H:%i:%S') 'create_time', CASE t.type WHEN 1 THEN 'AI' ELSE '人类' END 'type_str', CASE t.`status` WHEN 0 THEN '错误' WHEN 1 THEN '布子' WHEN 2 THEN '行棋' WHEN 3 THEN '飞子' WHEN 4 THEN '提掉中间两颗子' ELSE '系统操作' END 'status_str', message, t.cb_code, c.`value`, ui.real_name FROM trick t JOIN chess_info ci ON t.cb_code = ci.`code`
and t.cb_code=#{cbCode}
JOIN category c ON c.`key` = ci.category JOIN user_info ui ON ui.telephone = ci.user_id
AND (
${item} LIKE CONCAT('%',#{search.value},'%')
)
${item.columnName} ${item.orderBy}
